open Format
(* open Advent *)

let input = ""

let load input =
  Str.split (Str.regexp "") input

module SS = Set.Make(String);;

let find n chars =
  let rec aux i acc = function
  | [] -> failwith "notfound"
  | char::tail ->
    if List.length acc != (n - 1) then
      (* let () = printf "not full\n" in *)
      aux (i+1) (acc @ [char]) tail
    else
      (* let () = printf "full\n" in *)
      let new_acc = acc @ [char] in
      if (SS.of_list (acc @ [char]) |> SS.cardinal) = n then
        i+1 (* return 1 indexed *)
      else
        aux (i+1) (List.tl new_acc) tail
  in aux 0 [] chars
;;
 
let _ = load "mjqjpqmgbljsphdztnvjfqwrcgsmlb" |> find 4 |> printf "%d\n"
